@import "reset";
@import "helpers";
@import "layout";


$loadingbarthickness: 2px;

@mixin transition($whichpart) {
  transition: $whichpart ease-out;
}

::-moz-selection {
  /* Code for Firefox */
  color: $black;
  background: rgba(210, 216, 197, 0.99);
}

::selection {
  color: $black;
  background: rgba(210, 216, 197, 0.99);
}

/* width */
::-webkit-scrollbar {
  width: 0px;
}

/* Track */
::-webkit-scrollbar-track {
  background: $black;
}

/* Handle */
::-webkit-scrollbar-thumb {
  background: $lightgreen;
}

