  font-family: "Monument";
}

.start {
  display: flex;
  justify-content: center;
  flex-direction: column;
  height: 100vh;
  align-items: center;
  overflow: hidden;
  line-height: 1;

  .videobackground {
    position: absolute;
    top: 0;
