    letter-spacing: 35px;

    &.ourworld {
      margin-bottom: 6vh;
    }
  }

  .year {
    font-size: 28vw;
    animation: yeartext 7s linear infinite;
    position: relative;
    font-weight: bold;

    &:before {
      content: "imJahr";
      position: absolute;
      text-transform: uppercase;
      font-size: 2vw;
      top: 74.2%;
      left: 0.8%;
      letter-spacing: 42px;
      font-weight: normal;
      z-index: 100;
      color: $black;
    }
  }
}

.row {
  margin-right: 0rem;
  margin-left: 0rem;
}

.loadingbar1 {
  position: fixed;
  background: $lightgreen;
  top: 0;
  left: 0;
  width: $loadingbarthickness;
  height: 0;
  @include transition(height);
}

